#include <stdio.h>
#include <stdlib.h>
#include <string.h>

// project euler #8

long get_product( int start, int end );

char n[] = "";

int main() {
    int i, j;
    int length = strlen( n ); 
    long prod;
    long largest = 1;
    for ( i = 0, j = 13; i <= length && j <= length; i++, j++ ) {
        prod = get_product( i, j );
        if ( prod > largest ) {
            largest = prod;
        }
    }
    printf( "%ld\n", largest ); 
    return 0;
}

long get_product( int start, int end ) {
    long product = 1;
    int i;
    for ( i = start; i < end; i++ ) {
        product *= ( n[i] - '0' ); 
    }
    return product;
}
